import React, { useEffect, useState } from "react";
import Autocomplete from "@mui/material/Autocomplete";
import TextField from "@mui/material/TextField";
import { data } from "./AllOdiPlayers";

const SearchBar = () => {
  const [value, setValue] = useState("");
  const [item, setItem] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [filteredOptions, setFilteredOptions] = useState(item);

  const handleInputChange = (event, value) => {
    setInputValue(value);

    // Filter the options manually based on input
    const filtered = item.filter((option) =>
      option.label.toLowerCase().includes(value.toLowerCase())
    );
    setFilteredOptions(filtered);
  };

  var temp = [];
  data.map((player) => {
    temp.push({ label: player.Name, id: player.No });
  });

  useEffect(() => {
    if (value.length < 1) {
      return;
    }
    setItem(temp);
  }, [value]);

  return (
    <>
      <Autocomplete
        disablePortal
        options={item}
        getOptionLabel={(option) => option.label}
        inputValue={inputValue}
        onInputChange={handleInputChange}
        sx={{ width: 500, background: "#650e0e" }}
        renderInput={(params) => (
          <TextField
            {...params}
            fullWidth={true}
            label="Search for Player"
            shouldItemRender={(item, value) =>
              item.label.toLowerCase().indexOf(value.toLowerCase()) > -1
            }
            getItemValue={(item) => item.label}
            renderItem={(item, highlighted) => (
              <div
                key={item.label}
                style={{
                  backgroundColor: highlighted ? "#eee" : "transparent",
                }}
              >
                {item.label}
              </div>
            )}
            value={value}
            onChange={(e) => setValue(e.target.value)}
            disablePortal
          />
        )}
      />
      <h3>Suitable Matches:</h3>
      <ul>
        {filteredOptions.map((option) => (
          <li key={option.id}>{option.label}</li>
        ))}
      </ul>
    </>
  );
};

export default SearchBar;
