import React, { useEffect, useState } from "react";
import Autocomplete from "@mui/material/Autocomplete";
import TextField from "@mui/material/TextField";
import { data } from "./AllOdiPlayers";
import SearchedPlayer from "./SearchedPlayer";

const SearchBar = () => {
  const [value, setValue] = useState("");
  const [item, setItem] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [filteredOptions, setFilteredOptions] = useState(item);

  const handleInputChange = (event, value) => {
    setInputValue(value);

    // Filter the options manually based on input
    const filtered = item.filter((option) =>
      option.label.toLowerCase().includes(value.toLowerCase())
    );
    setFilteredOptions(filtered);
  };

  var temp = [];
  data.map((player) => {
    temp.push({
      label: player.Name,
      id: player.No,
      First: player.First,
      Last: player.Last,
      Mat: player.Mat,
      Inn: player.Inn,
      NO: player.NO,
      Runs: player.Runs,
      HS: player.HS,
      Avg: player.Avg,
      Balls: player.Balls,
      Mdn: player.Mdn,
      BowlRuns: player.BowlRuns,
      Wkt: player.Wkt,
      BBM: player.BBM,
      BowlAvg: player.BowlAvg,
      Ca: player.Ca,
      St: player.St,
    });
  });

  useEffect(() => {
    if (value.length < 1) {
      return;
    }
    setItem(temp);
  }, [value]);

  return (
    <>
      <Autocomplete
        disablePortal
        options={item}
        getOptionLabel={(option) => option.label}
        inputValue={inputValue}
        onInputChange={handleInputChange}
        sx={{ width: 500, background: "#650e0e" }}
        renderInput={(params) => (
          <TextField
            {...params}
            fullWidth={true}
            label="Search for Player"
            shouldItemRender={(item, value) =>
              item.label.toLowerCase().indexOf(value.toLowerCase()) > -1
            }
            getItemValue={(item) => item.label}
            renderItem={(item, highlighted) => (
              <div
                key={item.id}
                style={{
                  backgroundColor: highlighted ? "#eee" : "transparent",
                }}
              >
                {item.label}
              </div>
            )}
            value={value}
            onChange={(e) => setValue(e.target.value)}
            disablePortal
          />
        )}
      />

      <SearchedPlayer FilteredData={filteredOptions} />

      {/* <ul>
        {filteredOptions.map((option) => (
          <li key={option.id}>{console.log(option.label)}</li>
        ))}
      </ul> */}
    </>
  );
};

export default SearchBar;
