import React, { useEffect, useState } from "react";
import Autocomplete from "@mui/material/Autocomplete";
import TextField from "@mui/material/TextField";
import { data } from "./AllOdiPlayers";
import OdiPlayerList from "./OdiPlayerList";

const SearchBar = () => {
  const [isOpen, setISOpen] = useState(true);

  const toogleIsOpen = () => {
    setISOpen((isOpen) => !isOpen);
  };
  const [value, setValue] = useState("");
  const [item, setItem] = useState([]);
  const [itemData, setItemData] = useState([]);

  var temp = [];
  data.map((player) => {
    temp.push({ label: player.Name, id: player.No });
  });

  useEffect(() => {
    if (value.length < 1) {
      return;
    }
    setItem(temp);
  }, []);

  useEffect(() => {
    if (itemData) {
      setItem([itemData]);
    } else {
      setItem(temp);
    }
  }, [itemData]);

  console.table(temp);

  return (
    <>
      <div className="buttonStyle">
        <Button onClick={toogleIsOpen}>Hide All Players</Button>
      </div>

      <Autocomplete
        disablePortal
        options={item}
        sx={{ width: 500, background: "#650e0e" }}
        renderInput={(params) => (
          <TextField
            {...params}
            fullWidth={true}
            label="Search for Player"
            shouldItemRender={(item, value) =>
              item.label.toLowerCase().indexOf(value.toLowerCase()) > -1
            }
            getItemValue={(item) => item.label}
            renderItem={(item, highlighted) => (
              <div
                key={item.label}
                style={{
                  backgroundColor: highlighted ? "#eee" : "transparent",
                }}
              >
                {item.label}
              </div>
            )}
            value={value}
            onChange={(e) => {
              console.log(e.target.value);
            }}
            disablePortal
          />
        )}
      />
      {isOpen && <OdiPlayerList />}
    </>
  );
};

export default SearchBar;
