import React, { useEffect, useState } from "react";
import Autocomplete from "@mui/material/Autocomplete";
import TextField from "@mui/material/TextField";
import { data } from "./AllOdiPlayers";

const SearchBar = () => {
  const [value, setValue] = useState("");
  const [item, setItem] = useState([]);
  const [filteredOptions, setFilteredOptions] = useState(dataSet);

  const filterOptions = (options, state) => {
    const filtered = options.filter((option) =>
      option.label.toLowerCase().includes(state.inputValue.toLowerCase())
    );
    setFilteredOptions(filtered);
    return filtered; // Return filtered options to display in the autocomplete dropdown
  };

  var temp = [];
  data.map((player) => {
    temp.push({ label: player.Name, id: player.No });
  });

  useEffect(() => {
    if (value.length < 1) {
      return;
    }
    setItem(temp);
  }, [value]);

  return (
    <Autocomplete
      disablePortal
      options={item}
      filterOptions={filterOptions}
      sx={{ width: 500, background: "#650e0e" }}
      renderInput={(params) => (
        <TextField
          {...params}
          fullWidth={true}
          label="Search for Player"
          shouldItemRender={(item, value) =>
            item.label.toLowerCase().indexOf(value.toLowerCase()) > -1
          }
          getItemValue={(item) => item.label}
          renderItem={(item, highlighted) => (
            <div
              key={item.label}
              style={{
                backgroundColor: highlighted ? "#eee" : "transparent",
              }}
            >
              {item.label}
            </div>
          )}
          value={value}
          onChange={(e) => setValue(e.target.value)}
          disablePortal
        />
      )}
    />
  );
};

export default SearchBar;
